import React, { useState } from 'react';
import { postDataToAPI } from '../../apiService';
import Detection from './Detection';
import '../public/Lodge.css';
import { translate } from '@vitalets/google-translate-api';

const DescribeGrievance = () => {

    function runSpeechRecog() {
        var text_area = document.getElementById("write");
        var action = document.getElementById('action');
        let recognization = new webkitSpeechRecognition();
        recognization.onstart = () => {
            action.innerHTML = "Listening...";
        }
        recognization.onresult = (e) => {
            var transcript = e.results[0][0].transcript;
            var confidence = e.results[0][0].confidence;
            text_area.innerHTML = transcript;
            text_area.classList.remove("hide")
            action.innerHTML = "";
        }
        recognization.start();
    }
    
  const [showCard, setShowCard] = useState(false);
  const [grievanceText, setGrievanceText] = useState('');

  const handleGrievanceChange = (event) => {
    setGrievanceText(event.target.value);
  };

  const handleLodgeClick = async (event) => {
    event.preventDefault();
    if (grievanceText.trim() !== '') {
      try {
        // Translate the grievanceText to English
        const translation = await translate(grievanceText, { to: 'en' });

        // Send the translated text to your API
        const response = await postDataToAPI('nlp', { summary: translation.text });

        console.log('Response from your API:', response);

        setShowCard(true);
      } catch (error) {
        console.error('Error:', error);
      }
    } 
  };

  const handleCloseCard = () => {
    setShowCard(false);
  };

  return (
    <>
      <p id="fake" hidden></p>
      <form onSubmit={handleLodgeClick}>
        <div className="container-fluid mt-5 position-relative">
          <div className={`row ${showCard ? 'blur' : ''}`}>
            <div className="col-md-6 offset-md-3">
              <h2 className="mb-4">Describe Your Grievance</h2>
              <div className="input-group mb-3">
                <textarea
                  id="summary"
                  className="form-control border-2 border-dark"
                  rows="5"
                  placeholder="Enter your grievance here..."
                  value={grievanceText}
                  onChange={handleGrievanceChange}
                  required
                ></textarea>
              </div>
              <div className="d-flex align-items-center justify-content-between">
                <button
                  type="submit"
                  className="btn btn-warning w-75"
                  style={{ color: 'black' }}
                >
                  Lodge
                </button>
                <i className="fas fa-microphone text-black mic"></i>
              </div>
            </div>
          </div>
          {showCard && <Detection text={document.getElementById('summary').innerHTML} onClose={handleCloseCard} />}
        </div>
      </form>
    </>
  );
};

export default DescribeGrievance;
