import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Detection from './Detection';
import '../public/Lodge.css';
import Blob from '../../media/images/Blob.png';
import Signal from '../../media/images/Signal.png';
import { postDataToAPI } from '../../apiService';

function preprocess() {
  return new Promise(async (resolve) => {
    // ... (same as before)
  });
}

const DescribeGrievance = () => {
  const [showCard, setShowCard] = useState(false);
  const [grievanceText, setGrievanceText] = useState('');
  const [responseMessage, setResponseMessage] = useState('');
  const [listening, setListening] = useState(false);

  const handleGrievanceChange = (event) => {
    setGrievanceText(event.target.value);
  };

  const handleLodgeClick = async (event) => {
    event.preventDefault();
    if (grievanceText.trim() !== '') {
      const message = await preprocess();
      setResponseMessage(message);
      setShowCard(true);
    }
  };

  const handleCloseCard = () => {
    setShowCard(false);
  };

  const handleMicClick = () => {
    if ('webkitSpeechRecognition' in window) {
      const recognition = new window.webkitSpeechRecognition();
      recognition.lang = 'en-US';

      recognition.onstart = () => {
        setListening(true);
      };

      recognition.onend = () => {
        setListening(false);
      };

      recognition.onresult = (event) => {
        const transcript = event.results[0][0].transcript;
        setGrievanceText(transcript);
      };

      recognition.start();
    } else {
      alert('Speech recognition is not supported in this browser.');
    }
  };

  const handleMicStopClick = () => {
    recognition.stop();
  };

  useEffect(() => {
    if (!('webkitSpeechRecognition' in window)) {
      alert('Speech recognition is not supported in this browser.');
    }
  }, []);

  return (
    <>
      <div id="loader">
        <img src={Blob} className="blob" alt="Blob"></img>
        <img src={Signal} className="signal" alt="Signal"></img>
      </div>
      <p id="fake" hidden></p>
      <form onSubmit={handleLodgeClick}>
        <div className="container-fluid mt-5 position-relative">
          <div className={`row ${showCard ? 'blur' : ''}`}>
            <div className="col-md-6 offset-md-3">
              <h2 className="mb-4">Describe Your Grievance</h2>
              <div className="input-group mb-3">
                <textarea
                  id="summary"
                  className="form-control border-2 border-dark"
                  rows="5"
                  placeholder="Enter your grievance here..."
                  value={grievanceText}
                  onChange={handleGrievanceChange}
                  required
                ></textarea>
              </div>
              <div className="d-flex align-items-center justify-content-between">
                <button
                  type="submit"
                  className="btn btn-warning w-75"
                  style={{ color: 'black' }}
                >
                  Lodge
                </button>
                <button
                  type="button"
                  className={`btn btn-primary ${listening ? 'disabled' : ''}`}
                  onClick={listening ? handleMicStopClick : handleMicClick}
                >
                  {listening ? 'Stop Mic' : 'Start Mic'}
                </button>
              </div>
            </div>
          </div>
          {showCard && (
            <Detection
              text={document.getElementById('summary').innerHTML}
              onClose={handleCloseCard}
              responseMessage={responseMessage}
            />
          )}
        </div>
      </form>
    </>
  );
};

export default DescribeGrievance;
