import React, { useState } from "react";
import './css/Login.css';
import { Link, useNavigate } from 'react-router-dom'; // Import useNavigate for redirection
import { postDataToAPI } from "./apiService";

function Login() {
    const [redirectToDashboard, setRedirectToDashboard] = useState(false);
    const navigate = useNavigate(); // Get the navigate function for redirection

    function login() {
        let aadhar = document.getElementById('aadhar').value;
        let password = document.getElementById('password').value;
        postDataToAPI('login', {"aadhar": aadhar, "password": password})
        .then(data => {
            if (typeof data.message === 'number') {
                localStorage.setItem('user_id', data.message);
                setRedirectToDashboard(true);
            } else {
                console.error('Invalid user data');
            }
        })
        .catch(error => {
            console.error('Error fetching data:', error);
        });
    }

    // Redirect to /dashboard if redirectToDashboard is true
    if (redirectToDashboard) {
        navigate("/dashboard"); // Use navigate for redirection
    }

    return (
        <div className="login template d-flex justify-content-center align-items-center vh-100 back">
            <div className="form_container px-5 pt-4 pb-2 rounded bg-white">
                <form>
                    <h3 className="text-center">Login</h3>
                    <div className="mb-2">
                        <label htmlFor="aadhar">Aadhaar</label>
                        <input id="aadhar" type="text" placeholder="Enter Aadhaar" className="form-control"/>
                    </div>
                    <div className="mb-4">
                        <label htmlFor="password">Password</label>
                        <input id="password" type="password" placeholder="Enter Password" className="form-control"/>
                    </div>
                    <div className="d-grid">
                        <button className="btn btn-outline-warning" onClick={login}>Sign in</button>
                    </div>
                    <div className="d-grid">
                        <p className="text-center mt-3">
                            Not Registered? <Link to="/register" className="ms-2 color">Sign up</Link>
                        </p>
                    </div>
                </form>
            </div>
        </div>
    )
}

export default Login;
