import React, { useEffect, useState } from 'react';

const GoogleTranslate = ({ lang }) => {
  const [isInitialized, setIsInitialized] = useState(false);

  useEffect(() => {
    let googleTranslateTimer;

    function initializeGoogleTranslate() {
      window.google.translate.TranslateElement(
        {
          pageLanguage: 'en',
        },
        'google_translate_element'
      );

      googleTranslateTimer = setTimeout(() => {
        setIsInitialized(true);
      }, 1000);
    }

    if (!isInitialized) {
      initializeGoogleTranslate();
    } else {
      // Language selection logic
      if (lang === 'en' && window.google && window.google.translate) {
        const select = document.querySelector('.goog-te-combo');
        if (select) {
          // Check if the select element exists before setting its value
          select.value = 'en';
          select.dispatchEvent(new Event('change'));
        }
      }
    }

    return () => {
      clearTimeout(googleTranslateTimer);
    };
  }, [isInitialized, lang]);

  return <div id="google_translate_element"></div>;
};

export default GoogleTranslate;
