import React, { useEffect, useState } from "react";
import Table from "./Table";
import Header from "./Header";
import Link from "./Links";
import Cards from "./Cards";
import Footer from "./Footer";
import { fetchDataFromAPI } from "../../apiService";
import TrackingApp from './TrackingApp';
import { act } from "react-dom/test-utils";


const DashboardUser = () => {
  const [jsonData, setJsonData] = useState(null);
  const [activeCount, setActiveCount] = useState(0);
  const [totalCount, setTotalCount] = useState(0);
  const [completedCount, setCompletedCount] = useState(0);
  const id = localStorage.getItem('user_id');
  const url = `grievances/u${id}`;
  useEffect(() => {
    fetchDataFromAPI(url)
      .then(json => {
        // Handle the response data
        console.log('Data fetched:', json.message);
        setJsonData(json.message);
        var ccount = 0;
        var acount = 0;
        jsonData.forEach(jsonObj => {
          if (jsonObj.status === "completed") {
              completedCount++;
          } else if (jsonObj.status === "active") {
              activeCount++;
          }
        });
        setActiveCount()
        setTotalCount(jsonData.length);
      })
      .catch(error => {
        // Handle any errors
        console.error('Error fetching data:', error);
      });
  }, [url]);

  var c1 = {activeCount};
  var c2 = {completedCount};
  var c3 = {totalCount};
    return (
        <div className="App">
          <Header />
          <div>
            <Link user="test" />
            <Cards c1={c1} c2={c2} c3={c3} />
            <TrackingApp />
            <Table data={jsonData}/>
          </div>
          <Footer c1={c1} c2={c2} c3={c3} />
        </div>
      );
}

export default DashboardUser;
