// App.js
import React from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import PrivateRoute from './Components/PrivateRoute';
import Login from './Components/Login';
import SignUp from './Components/Signup';
import Dashboard from './Components/Dashboard';

const App = () => {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<Navigate to="/login" />} />
        <Route path="/login" element={<Login />} />
        {/* <Route path="/wlogin" element={<PrivateRoute component={Worker_Login} /> */}
        <Route path="/register" element={<SignUp />} />
        {/* PrivateRoute equivalent */}
        <Route path="/dashboard" element={<PrivateRoute component={Dashboard} />} />
          {/* <Route path="/worker" element={<PrivateRoute component={WorkerDashboard} />} /> */}
      </Routes>
      <div className="position-absolute bottom-0 end-0 m-3 trans">
                <label for="language-select">Select Language:</label>
                <GoogleTranslate lang="hi" />
            </div>
    </Router>
  );
};

export default App;
