import React, { useState } from 'react';
import { postDataToAPI } from '../../apiService';
import Detection from './Detection';
import '../public/Lodge.css';

function preprocess(grievanceText) {
  return new Promise(async (resolve) => {
    var fake = document.getElementById("fake");
    var select = document.querySelector(".goog-te-combo");
    var lang = select.value; 
    fake.innerHTML = grievanceText;
    select.value = "en";
    select.dispatchEvent(new Event("change"));

    try {
      // Translate the summary back to English
      const translation = await translate(grievanceText, { from: lang, to: 'en' });
      const englishSummary = translation.text;

      // Send the Axios request with the translated summary
      const response = await postDataToAPI('nlp', { summary: englishSummary });
      console.log('Response from axios:', response.message);

      setTimeout(() => {
        select.dispatchEvent(new Event("change"));
        select.value = lang;
        select.dispatchEvent(new Event("change"));
        resolve();
      }, 500);
    } catch (error) {
      console.error('Error in axios request:', error);
      resolve();
    }
  });
}

const DescribeGrievance = () => {
  const [showCard, setShowCard] = useState(false);
  const [grievanceText, setGrievanceText] = useState('');

  const handleGrievanceChange = (event) => {
    setGrievanceText(event.target.value);
  };

  const handleLodgeClick = async (event) => {
    event.preventDefault();
    if (grievanceText.trim() !== '') {
      await preprocess(grievanceText); // Call preprocess and wait for it to complete
      setShowCard(true);
    } 
  };

  const handleCloseCard = () => {
    setShowCard(false);
  };

  return (
    <>
      <p id="fake" hidden></p>
      <form onSubmit={handleLodgeClick}>
        <div className="container-fluid mt-5 position-relative">
          <div className={`row ${showCard ? 'blur' : ''}`}>
            <div className="col-md-6 offset-md-3">
              <h2 className="mb-4">Describe Your Grievance</h2>
              <div className="input-group mb-3">
                <textarea
                  id="summary"
                  className="form-control border-2 border-dark"
                  rows="5"
                  placeholder="Enter your grievance here..."
                  value={grievanceText}
                  onChange={handleGrievanceChange}
                  required
                ></textarea>
              </div>
              <div className="d-flex align-items-center justify-content-between">
                <button
                  type="submit"
                  className="btn btn-warning w-75"
                  style={{ color: 'black' }}
                >
                  Lodge
                </button>
                <i className="fas fa-microphone text-black mic"></i>
              </div>
            </div>
          </div>
          {showCard && <Detection text={document.getElementById('summary').innerHTML} onClose={handleCloseCard} />}
        </div>
      </form>
    </>
  );
};

export default DescribeGrievance;
