import React, { useState } from 'react';
import Detection from './Detection';

const DescribeGrievance = () => {
  const [showCard, setShowCard] = useState(false);

  const handleLodgeClick = () => {
    setShowCard(true);
  };

  const handleCloseCard = () => {
    setShowCard(false);
  };

  return (
    <div className="container-fluid mt-5 position-relative">
      <div className={`row ${showCard ? 'blur' : ''}`}>
        <div className="col-md-6 offset-md-3">
          <h2 className="mb-4">
            Describe Your Grievance
          </h2>
          <div className="input-group mb-3">
            <textarea
              className="form-control bg-warning border border-dark"
              rows="5"
              placeholder="Enter your grievance here..."
            ></textarea>
          </div>
          <button
            className="btn btn-warning"
            style={{ color: 'black' }}
            onClick={handleLodgeClick}
          >
           Lodge
          </button>
        </div>
      </div>
      {showCard && <Detection onClose={handleCloseCard} />}
    </div>
  );
};

export default DescribeGrievance;
