import React, { useState } from "react";
import "../public/";

const BubbleTrackerStatus = ({ bubbles, variableValue,gid,dept,date,summary}) => {
  const spacing = 100 / (bubbles.length + 1);

  // State to track the visibility of the dropdown paragraph
  const [isDropdownVisible, setIsDropdownVisible] = useState(false);

  // Function to toggle the visibility of the dropdown paragraph
  const toggleDropdown = () => {
    setIsDropdownVisible(!isDropdownVisible);
  };

  const renderBubbles = () => {
    return bubbles.map((bubble, index) => {
      const isCompleted = variableValue >= index + 1;

      return (
        <>
          <div
            key={bubble.id}
            className={`flipkart-bubble${isCompleted ? " completed" : ""}`}
            style={{
              left: `${spacing * (index + 1)}%`,
              top: `${bubble.y}px`,
            }}
          >
            {isCompleted && <div className="tick-mark">&#10004;</div>}
            <p className="flipkart-labels">
              {(() => {
                switch (bubble.id) {
                  case 1:
                    return "Lodged";
                  case 2:
                    return "Reviewed";
                  case 3:
                    return "Assigned";
                  case 4:
                    return "Task Done";
                  case 5:
                    return "Verified";
                  default:
                    return "none";
                }
              })()}
            </p>
          </div>
        </>
      );
    });
  };
  return (
    <div className="flipkart-bubble-container">
    <div className="bubble-para">
    <p className='para'>G_Id: {gid}</p>
    <p className='para'>Dept: {dept}</p>
    <p className='para'>Date: {date}</p>
    </div>
      <div className="flipkart-bubble-box" onClick={toggleDropdown}>
        <div className="flipkart-bubble-line" style={{ width: `${spacing * variableValue}%` }}></div>
        {renderBubbles()}
      </div>
      {isDropdownVisible && (
        <div className="bubble-dropdown">
          <p>{summary}</p>
        </div>
      )}
    </div>
  );
};



export default BubbleTrackerStatus;
