import React, { useEffect } from 'react';

const GoogleTranslate = ({ lang }) => {
  useEffect(() => {
    // Initialize Google Translate once when the JavaScript file is loaded
    if (!window.googleTranslateInitialized) {
      window.googleTranslateElementInit = function () {
        window.google.translate.TranslateElement(
          {
            pageLanguage: 'en', // Set the default language to English
          },
          'google_translate_element'
        );

        // Delay setting the language to the specified language
        setTimeout(() => {
          var select = document.querySelector(".goog-te-combo");
          select.value = lang;
          select.dispatchEvent(new Event("change")); // Trigger the change event
        }, 1000);
      };

      // Mark Google Translate as initialized to prevent multiple initializations
      window.googleTranslateInitialized = true;

      // Load the Google Translate script
      const script = document.createElement('script');
      script.src = '//translate.google.com/translate_a/element.js?cb=googleTranslateElementInit';
      script.async = true;
      document.body.appendChild(script);
    }
  }, [lang]);

  return <div id="google_translate_element"></div>;
};

export default GoogleTranslate;
