// apiService.js
import axios from 'axios';

// Function to make a GET request
function fetchDataFromAPI(url_endpoint) {
  return axios.get(url_endpoint)
    .then(response => {
      
      return response.data;
    })
    .catch(error => {
      throw error;
    });
}

// Function to make a POST request
function postDataToAPI(url_endpoint, data) {
  return axios.post(url_endpoint, data)
    .then(response => {
      return response.data;
    })
    .catch(error => {
      throw error;
    });
}

// Function to make a PUT request
function putDataToAPI(url_endpoint, data) {
  return axios.put(url_endpoint, data)
    .then(response => {
      return response.data;
    })
    .catch(error => {
      throw error;
    });
}

export { fetchDataFromAPI, postDataToAPI, putDataToAPI };
