import React, { useState, useEffect } from 'react';
import { Navigate } from 'react-router-dom';
import { fetchDataFromAPI } from './apiService';

const Dashboard = () => {
  const [session, setSession] = useState(null);
  const [isLoading, setIsLoading] = useState(true); // Add a loading state

  useEffect(() => {
    // Fetch data from the API
    fetchDataFromAPI("http://127.0.0.1:8000/api/usession")
      .then(apiData => {
        // Check if the response has message: true
        if (apiData.message === true) {  
          setSession(apiData.message);
        } else {
          // Set the session to a falsy value to indicate a failed response
          setSession(false);
        }
      })
      .catch(error => {
        console.error('Error fetching data:', error);
        // Set the session to a falsy value on error

      })
      .finally(() => {
        // Set isLoading to false when the response is received or on error
        setIsLoading(false);
      });
  }, []);

  // Render the Dashboard component conditionally based on isLoading
  if (isLoading) {
    return <div>Loading...</div>; // Show loading indicator
  }

  // After isLoading is false, check the session and render accordingly
  if (session === true) {
    return (
      <div>
        {/* Your Dashboard content here */}
        <h1>Welcome to the Dashboard</h1>
        {/* Add your Dashboard content */}
      </div>
    );
  } else if (session === false) {
    // Redirect to login page if session is false
    return <Navigate to="/login" replace />;
  }

  return null; // Add any additional error handling logic here
};

export default Dashboard;
