import React,{useState} from 'react';
import '../public/';
import BubbleTrackerStatus from './components/BubbleTrackerStatus';
import jsonData from './components/data.json'

const initialBubbles = [
  { id: 1, x: 20, y: 75 },
  { id: 2, x: 40, y: 75 },
  { id: 3, x: 60, y: 75 },
  { id: 4, x: 80, y: 75 },
  { id: 5, x: 100, y: 75 },
];

  function StatusTracks() {
    const [searchQuery, setSearchQuery] = useState('');
  const handleSearchChange = (event) => {
    setSearchQuery(event.target.value);
  };
  const filteredData = jsonData.filter((item) => {
    const lowerCaseQuery = searchQuery.toLowerCase();
    return (
      item.Department.toLowerCase().includes(lowerCaseQuery) ||
      item.Date.includes(searchQuery) || 
      item.Grievance_ID.toString().includes(searchQuery) 
    );
  });
    return (
      <div className="App">
      <center>
      <input
        id='searchingbar'
        type="text"
        placeholder="Search"
        value={searchQuery}
        onChange={handleSearchChange}
      />
      </center>
      {filteredData.map((item, index) => (
        <BubbleTrackerStatus
          key={index}
          bubbles={initialBubbles}
          variableValue={item.Stage}
          gid={item.Grievance_ID}
          dept={item.Department}
          date={item.Date}
          summary={item.Summary}
        />
      ))}
    </div>
    );
  }



export default StatusTracks;
