import React, { useState, useHistory } from 'react';
import './css/Login.css';
import { Link } from 'react-router-dom';
import axios from 'axios';

const axiosInstance = axios.create({
  baseURL: 'http://localhost:8000/api/', // Replace with your API server URL
  withCredentials: true, // Enable sending and receiving cookies
  headers: {
    'Content-Type': 'application/json',
  },
});

function loginUser(aadhaar, password) {
    return axiosInstance.post('login', { aadhaar, password })
      .then(response => {
        return response.data;
      })
      .catch(error => {
        throw error;
    });
}

function Login() {
    const [aadhaar, setAadhaar] = useState('');
    const [password, setPassword] = useState('');
    const history = useHistory();

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
        // Send a POST request to authenticate the user
        const response = await loginUser(aadhaar, password);

        // If authentication is successful, redirect to the Dashboard
        if (response.message === true) {
            history.push('/dashboard');
        } else {
            // Handle authentication error (e.g., display an error message)
            console.error('Authentication failed');
        }
        } catch (error) {
        // Handle network or other errors
        console.error('Error:', error);
        }
    };

    return (
        <div className="login template d-flex justify-content-center align-items-center vh-100 back">
            <div className="form_container px-5 pt-4 pb-2 rounded bg-white">
                <form onsubmit={handleSubmit}>
                    <h3 className="text-center">Login</h3>
                    <div className="mb-2">
                        <label htmlFor="aadhaar">Aadhaar</label>
                        <input type="text" placeholder="Enter Aadhaar" className="form-control"/>
                    </div>
                    <div className="mb-4">
                        <label htmlFor="password">Password</label>
                        <input type="password" placeholder="Enter Password" className="form-control"/>
                    </div>
                    <div className="d-grid">
                        <button className="btn btn-outline-warning">Sign in</button> 
                        {/* Database Login Link */}
                    </div>
                    <div className="d-grid">
                        <p className="text-center mt-3">
                            Not Registered? <Link to="/register" className="ms-2 color">Sign up</Link>
                        </p>
                    </div>
                </form>
            </div>
        </div>
    )
}

export default Login
