// Layout.js
import React from 'react';
import GoogleTranslate from './GoogleTranslate';

const Layout = ({ children }) => {
  useEffect(() => {
    function googleTranslateElementInit() {
      window.google.translate.TranslateElement(
        {
          pageLanguage: 'en', // Set the default language to English
        },
        'google_translate_element'
      );
    }

    window.onload = function () {
      googleTranslateElementInit();

      // Delay setting the language to English
      setTimeout(() => {
        var select = document.querySelector(".goog-te-combo");
        select.value = lang;
        select.dispatchEvent(new Event("change")); // Trigger the change event
      }, 1000);
    };
  }, [lang]);
  return (
    <div>
      <div className="position-absolute bottom-0 end-0 m-3 trans">
        <label for="language-select">Select Language:</label>
        <GoogleTranslate lang="en" />
      </div>
      {children}
    </div>
  );
};

export default Layout;
