import React,{Component} from "react";
import $ from "jquery";


class Table extends Component{
  constructor(props) {
    super(props);
    this.state = {
      searchQuery: '',
    };
  }

  componentDidMount() {
    $(() => {
      $('#table').bootstrapTable()
    })
  }

  handleSearch = (e) => {
    this.setState({ searchQuery: e.target.value });
  };

  render() {
    const { data } = this.props;
    const { searchQuery } = this.state;

    // Filter the data based on the searchQuery
    const filteredData = data.filter((item) =>
      Object.values(item)
        .join(' ')
        .toLowerCase()
        .includes(searchQuery.toLowerCase())
    );
    return(
        <main className="w-75">
        <label htmlFor="search">Search: </label>
        <input
          type="text"
          id="search"
          value={searchQuery}
          onChange={this.handleSearch}
            
          />
          <table
            className="table"
            id="example"
            data-filter-control="true"
          >
            <thead>
              <tr>
                <th data-field="Date" data-filter-control="select">
                  Date
                </th>
                <th>Grievance ID</th>
                <th>Worker ID</th>
                <th>Location</th>
                <th data-field="Priority" data-filter-control="select">
                  Priority
                </th>
                <th data-field="Department" data-filter-control="select">
                  Department
                </th>
                <th>Status</th>
                <th>Stage</th>
              </tr>
            </thead>

            <tbody id="tableBody">
            {filteredData.map((item) => (
            <tr>
              <td>{item.Date}</td>
              <td>{item.Grievance_ID}</td>
              <td>{item.Worker_ID}</td>
              <td>{item.Location}</td>
              <td>{item.Priority}</td>
              <td>{item.Department}</td>
              <td>{item.Status}</td>
              <td>{item.Stage}</td>
            </tr>
          ))}
            </tbody>
          </table>
        </main>
    )
  }

}

export default Table;
