import React, { useEffect } from 'react';

const GoogleTranslate = () => {
  useEffect(() => {
    function waitForElementToExist(selector) {
        return new Promise(resolve => {
            if (document.querySelector(selector)) {
                return resolve(document.querySelector(selector));
            }

            const observer = new MutationObserver(() => {
                if (document.querySelector(selector)) {
                    resolve(document.querySelector(selector));
                    observer.disconnect();
                }
            });

            observer.observe(document.body, {
                subtree: true,
                childList: true,
            });
        });
    }

    var lang = "hi"; // from database
        
    function googleTranslateElementInit() {
        google.translate.TranslateElement({
            pageLanguage: 'en', // Set the default source language to English
                
                // layout: google.translate.TranslateElement.InlineLayout.SIMPLE,
            }, 'google_translate_element');

            waitForElementToExist('#google_translate_element').then(element => {
                setTimeout(function () {
                    var select = document.querySelector(".goog-te-combo");
                    select.value = lang; // Select Hindi
                    select.dispatchEvent(new Event("change")); // Trigger the change event
                    document.getElementById("preprocess").disabled = false;
                    document.getElementById("speech").disabled = false;
                }, 1000);
            });
        }
  }, []);

  return (
    <div id="google_translate_element"></div>
  );
};

export default GoogleTranslate;
